function addAqiData(){var t=document.getElementById("aqi-city-input"),e=document.getElementById("aqi-value-input");aqiData[t.value]=Number(e.value)}function renderAqiList(){var t=document.getElementById("aqi-table");t.innerHTML="<tbody><tr><td>城市</td><td>空气质量</td><td>操作</td></tr></tbody>";for(var e in aqiData)t.tBodies[0].innerHTML+="<tr><td>"+e+"</td><td>"+aqiData[e]+"</td><td><button>删除</button></td>"}function addBtnHandle(){var t=document.getElementById("aqi-city-input"),e=document.getElementById("aqi-value-input");""==t.value||""==e.value||isNaN(e.value)||(addAqiData(),renderAqiList())}function delBtnHandle(){document.getElementById("aqi-table").tBodies[0];return delete aqiData[this.parentNode.parentNode.firstElementChild.innerHTML],renderAqiList(),!1}function init(){window.onload=function(){var t=document.getElementById("add-btn");t.addEventListener("click",addBtnHandle,!1);var e=document.getElementById("aqi-table");e.addEventListener("click",function(t){"button"==t.target.tagName.toLowerCase()&&delBtnHandle.call(t.target)},!1)}}var aqiData={};init();