"use strict";function renderQueue(){oData.innerHTML="";for(var e=0;e<queue.length;e++)oData.innerHTML+="<li>"+queue[e]+"</li>"}function changeQueue(){var e=Number(this.inpBox.value),t=event.target.name;"button"==event.target.type&&("outLeft"==t?alert("您删除的元素是"+queue.shift()):"outRight"==t?alert("您删除的元素是"+queue.pop()):""!=e&&/\d+/.test(e)?"toLeft"==t?queue.unshift(e):queue.push(e):alert("输入不合法，请重新输入！"),renderQueue())}function delData(){var e=oData.getElementsByTagName("li");if("LI"==event.target.tagName){for(var t=0;t<e.length;t++)if(event.target==e[t]){queue.splice(t,1);break}renderQueue()}}function init(){var e=document.getElementById("form1");e.addEventListener("click",changeQueue,!1),oData.addEventListener("click",delData,!1)}var oData=document.getElementById("data"),queue=[];init();